package Day06_ArithmeticOperators;

public class Task2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 *  Write a Java program to convert Fahrenheit to Celcius 
		 *  F=9*C/5+32

		 */
		

	double F=61;
	double C = (F-32) * 5/9;
	System.out.println(C);
	
	System.out.println((int) C);
	
	}
	
}