package Day10_IfStatement;

public class WithoutCurlyBrace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		if (false) {
			System.out.println("Today is friday");
			System.out.println("Tomorrow is day off.");
		}
		
	//	if (false) // without curly brace, it can contain only one statement, or one single line of code, or one single semicolon;
		
		if (true)
			System.out.println("Hi");
	
		else if (true) // without curly brace, it can contain only one statement, or one single line of code, or one single semicolon;
			System.out.println("cool");
	
		
		
		//else block is not mandatory. but if you declare else if block, else block is mandatory.
		
		
		
	
	}

}
