public class QuizReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Kahoot
		int n = 'B';
		System.out.println(n); // corresponding number of the character B will be printed.
		
		System.out.println("================");
		if (n>128 || n<=129) {
		// false  || true
			
			System.out.println('B'); // prints out the char character
		}
		
		else {
			System.out.println('A');
		}
	
		// Quiz
		

	}

}
