package assignments;

public class Task05_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
1. declare a variable called num.
2. write a program that can check if number is positive or negative or equals to zero.

for example:

num  = 1
print:
"positive"

num = -6
print:
"negative"

num = 0
print:
"zero"
		 */
		
		int num = 1;
		
		if (num==0) {
		System.out.println("zero");
		}
		
		else if (num<0) {
			System.out.println("negative");
		}
		
		else {
		System.out.println("positive");
			}
		
		}
	}
